org 0x7e00
jmp 0x0000:start

%macro setText 3
    mov ah, 02h  ; Setando o cursor
	mov bh, 0    ; Pagina 0
	mov dh, %1   ; Linha
	mov dl, %2   ; Coluna
	int 10h

    mov si, %3
    call printf
%endmacro

start:
	call initVideo

	call draw_dino
    setText 11, 13, offline
    setText 14, 3, text_fun1
    setText 15, 3, text_fun2
    setText 18, 12, try
    call draw_esq_button

    mov ah, 0
    int 16h

    cmp al, 27
    je start

jmp $

printf:
	lodsb
	cmp al,0
	je .end

	mov ah, 0eh
	mov bl, 15
	int 10h

	mov dx, 0
	jmp printf

    .end:
    mov ah, 0eh
    mov al, 0xd
    int 10h
    mov al, 0xa
    int 10h
ret

initVideo:
	mov ah, 00h
	mov al, 13h
	int 10h
    ; Set background white
    mov ah, 0ch 
	mov al, 0fh
	mov bh, 0
	mov cx, 0
	mov dx, 0
	.draw_seg:
		int 10h
		inc cx
		cmp cx, 320
		je .jump_row
		jne .draw_seg
	.back_column:
		mov cx, 0
		jmp .draw_seg
	.jump_row:
		inc dx
		cmp dx, 200
		jne .back_column
	ret


draw_dino: 
    mov si, dino
	mov dx, 0           
	mov bx, si
	add si, 2
	.for1:
		cmp dl, byte[bx+1]
		je .endfor1
		mov cx, 0       
		.for2:
			cmp cl, byte[bx]
			je .endfor2
			lodsb
            push dx ; Draw pixel
            push cx
            mov ah, 0ch
            add dx, 50
            add cx, 130
            int 10h
            pop cx
            pop dx
			inc cx
			jmp .for2
		.endfor2:
		inc dx
		jmp .for1
	.endfor1:
	ret

draw_esq_button:
    mov si, esc_button
	mov dx, 0            ; Y
	mov bx, si
	add si, 2
	.for1:
		cmp dl, byte[bx+1]
		je .endfor1
		mov cx, 0        ; X
    .for2:
        cmp cl, byte[bx]
        je .endfor2
        lodsb
        push dx
        push cx
        mov ah, 0ch
        add dx, 2
        add cx, 2
        int 10h
        pop cx
        pop dx
        inc cx
        jmp .for2
    .endfor2:
        inc dx
        jmp .for1
    .endfor1:
ret

